INSERT IGNORE INTO `sys_rule_deployed` (`objid`, `ruletext`) VALUES ('RUL-2486b0ca:146fff66c3e:-4192', '\npackage bldgassessment.CALC_DEPRECIATION;\nimport bldgassessment.*;\nimport java.util.*;\nimport com.rameses.rules.common.*;\n\nglobal RuleAction action;\n\nrule \"CALC_DEPRECIATION\"\n	agenda-group \"DEPRECIATION\"\n	salience 50000\n	no-loop\n	when\n		\n		\n		RPU: rptis.bldg.facts.BldgRPU (  DPRATE:depreciation ) \n		\n		BU: rptis.bldg.facts.BldgUse (  BMV:basemarketvalue,ADJUSTMENT:adjustment,useswornamount == false  ) \n		\n	then\n		Map bindings = new HashMap();\n		\n		bindings.put(\"RPU\", RPU );\n		\n		bindings.put(\"DPRATE\", DPRATE );\n		\n		bindings.put(\"BMV\", BMV );\n		\n		bindings.put(\"BU\", BU );\n		\n		bindings.put(\"ADJUSTMENT\", ADJUSTMENT );\n		\n	Map _p0 = new HashMap();\r\n_p0.put( \"bldguse\", BU );\r\n_p0.put( \"expr\", (new ActionExpression(\"@ROUND( (BMV + ADJUSTMENT ) * DPRATE   / 100.0  , 2)\", bindings)) );\r\naction.execute( \"calc-bldguse-depreciation\",_p0,drools);\r\n\nend\n\n\n	');
